import React, {Component} from 'react';

class Now extends Component{
    update_date = () => {
        var today = new Date();
        document.getElementById("current_date").innerHTML = today.getMonth();
    }
    componentDidMount() {this.interval = setTimeout(() => this.loop(),1)}
    render(){
        return(
            <p id="current_date"></p>
        );
    }
}

export default Now
