import React, { Component } from 'react';
import './Livelocation.css';


class Livelocation extends Component{
    append_zero = (number) => {
        if (number < 10){
            number = '0' + number;
        }
        return number;
    }
    convert_day = (day) => {
        var the_day = '';
        if( day == 1){
            the_day = 'Monday';
        } else if( day == 2){
            the_day = 'Tuesday';
        } else if( day == 3){
            the_day = 'Wednesday';
        } else if( day == 4){
            the_day = 'Thursday';
        } else if( day == 5){
            the_day = 'Friday';
        } else if( day == 6){
            the_day = 'Saturday';
        } else {
            the_day = 'Sunday';
        }

        return the_day;
    }
    render(){
        return(
            <div>
                <h1 class="title">Real time locations</h1>
                <p id="date"></p>
            </div>
        )
    }
}

export default Livelocation
